    where (appl_type="休假" or appl_type="出差" or appl_type="请假") and user_number = #{userNumber,jdbcType=INTEGER}

   

    update appl 

       

    set appl_status = '2' 

     

    where (appl_type="休假" or appl_type="出差" or appl_type="请假") and user_number = #{userNumber,jdbcType=INTEGER}
