mapper.select:
SELECT COUNT(attd.attd_begintime) as attday 
FROM attd 
WHERE  date_format(attd_begintime,'%Y-%m') =date_format(now(),'%Y-%m') and user_number= #{usernumber,jdbcType=VARCHAR}

SELECT COUNT(attd.attd_vaction_begintime) as attdVactionDay 
FROM attd 
WHERE  date_format(attd_vaction_begintime,'%Y-%m') =date_format(now(),'%Y-%m') and user_number= #{usernumber,jdbcType=VARCHAR}

SELECT COUNT(attd.attd_travel_begintime) as attdTravelDay 
FROM attd 
WHERE  date_format(attd_travel_begintime,'%Y-%m') =date_format(now(),'%Y-%m') and user_number= #{usernumber,jdbcType=VARCHAR}
