mapper.update:
  
   update appl
       
   set appl_status = '1'  
   
   where user_number = #{usernumber,jdbcType=VARCHAR}

  
   update appl 
      
   set appl_status = '2' 
    
   where user_number = #{usernumber,jdbcType=VARCHAR}

  
   update appl 
      
   set appl_status = '3' 
    
   where user_number = #{usernumber,jdbcType=VARCHAR}
